import React from 'react'
import MinyCard from '../MinyCard';
import data from '../Category/db';

export default function AllCards(props) {
    const { dataCard } = props;
    return (
        <div key={dataCard.categoryId}>
              {dataCard.categoryId ? (
                  <div className="allCards">
                    {dataCard.map((elem) => {
                      if (elem.categoryId) {
                        dataCard.filter((i) => {
                          return i.category === elem.category;
                          
                        });
                        return <div> <MinyCard dataCard={data} /></div>;
                      }
                    })}
                  </div>
                ) : null}
        </div>
    )
}
