import React from "react";
import {Button,Card} from "react-bootstrap";
import {  category, AllData } from '.'



export default function MinyCard(props) {
  const { dataCard } = props;

  return (
    <Card>
    <Card.Img variant="top" src={dataCard.Image} />
    <Card.Body>
      <Card.Title className="card-title"><h3 className="small-card-title">{dataCard.Title}</h3></Card.Title>
      <Card.Subtitle>
        {dataCard.Provider ? <h4 className="card-provider">{dataCard.Provider}</h4> : <br />}
      </Card.Subtitle>
      <Card.Text>{dataCard.City ? <h4 className="card-city">{dataCard.City}</h4> : <br />}</Card.Text>
      {dataCard.button ? <Button>View collection</Button> : null}
    </Card.Body>
    <Card.Footer>{dataCard.price ? dataCard.price + "$" : <br />}</Card.Footer>
  </Card>

   )};
