import React from "react";

export default function MinyCard(props) {
  const { dataCards } = props;
  return ( <div
    // key={elem.categoryId}
    // id={elem.id}
    // className="allCard"
  >
    <Card className="cards">
      <Card.Img variant="top" src={elem.img} />
      {elem.newTOSite ? "New TO Site" : " "}
      <Card.Body>
        <Card.Title>
          <h3>
            {" "}
            {elem.title.replace(
              /^(.{20}[^\s]*).*/,
              "$1"
            )}
          </h3>

          <h4>
            {" "}
            {elem.subTitle.replace(
              /^(.{20}[^\s]*).*/,
              "$1"
            )}
          </h4>
          <h6>{elem.country}</h6>
          <div className="overlay">
            <div className="text">
              {" "}
              {elem.title}
            </div>
          </div>
        </Card.Title>
        <Card.Text className="cardText">
          <h6>
            {" "}
            {elem.numberBough
              ? ` ${elem.numberBough} bought`
              : " "}
          </h6>
          <div className="price">
            <span>
              {elem.price ? "Form" : " "}{" "}
            </span>
            <br />
            <h4>
              {elem.price
                ? ` $ ${elem.price} `
                : " "}{" "}
            </h4>
          </div>
        </Card.Text>
        {elem.button ? (
          <Button
            className="cardButton"
            variant="outline-secondary"
            id="button-addon2"
          >
            View Collection
          </Button>
        ) : null}
      </Card.Body>
    </Card>
  </div>);
}
