import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import { MDBCol, MDBRow } from "mdbreact";
import "./Category.css";
import Card from "react-bootstrap/Card";
import MinyCard from "../MinyCard";
import data from "./db";
import Buttons from "../Buttons";

function Category(props) {
  const { dataCard } = props;
  // const category=["Featured Auckland deals","Collections","Escapes","Activities, Events & Outdoors"]
  return (
    <div className="Category">
        <div className="all-Categories">
          <div className="card">
                {dataCard.map((elem) => {
                  return (
                    <div key={elem.categoryId}>
                      {elem.categoryId ? (
                        <div className="allCards">
                          {dataCard.map((elem) => {
                            if (elem.categoryId) {
                              dataCard.filter((i)=>{
                                {/* console.log( "++++++++++++++++++++++",i.category) */}
                                return i.category === elem.category
                              }
                              )
                              return (
                                <div>
                                  <MDBRow>
                                  <Buttons dataCard={data} />
                                  <MinyCard dataCard={data} />
                                  </MDBRow>
                                 
                                </div>
                              );
                            }
                          })}
                        </div>
                      ) : null}
                    </div>
                  );
                })}
    
            <MDBRow></MDBRow>
          </div>
          <div className="card">
            <MDBRow>
              <MDBCol md="3">
                <h2 style={{ textAlign: "left" }}>Collections</h2>
              </MDBCol>
              <MDBCol md="3">
                {" "}
                <Button
                  className="CategoriesButton"
                  variant="outline-secondary"
                  id="button-addon2"
                >
                  View More
                </Button>
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <div className="allCards">
                {dataCard.map((elem, index) => {
                  if (elem.category === "Collections") {
                    return (
                      <div key={index} id={elem.id} className="allCard">
                        <Card className="cards">
                          <Card.Img variant="top" src={elem.img} />
                          {dataCard.newTOSite ? "New TO Site" : " "}
                          <Card.Body>
                            <Card.Title>
                              <h3>
                                {" "}
                                {elem.title.replace(/^(.{20}[^\s]*).*/, "$1")}
                              </h3>
                              <h4>
                                {" "}
                                {elem.subTitle.replace(
                                  /^(.{20}[^\s]*).*/,
                                  "$1"
                                )}
                              </h4>
                              <h6>{elem.country}</h6>
                              <div className="overlay">
                                <div className="text"> {elem.title}</div>
                              </div>
                            </Card.Title>
                            <Card.Text className="cardText">
                              <h6>
                                {" "}
                                {elem.numberBough
                                  ? ` ${elem.numberBough} bought`
                                  : " "}
                              </h6>
                              <h4>
                                <span>From</span>
                                <br /> {elem.price
                                  ? ` $ ${elem.price} `
                                  : " "}{" "}
                              </h4>
                            </Card.Text>
                            {elem.button ? (
                              <Button
                                className="cardButton"
                                variant="outline-secondary"
                                id="button-addon2"
                              >
                                View Collection
                              </Button>
                            ) : null}
                          </Card.Body>
                        </Card>
                      </div>
                    );
                  }
                })}
              </div>
            </MDBRow>
          </div>
          <div className="card">
            <MDBRow>
              <MDBCol md="3">
                <h2 style={{ textAlign: "left" }}>Escapes </h2>{" "}
              </MDBCol>
              <MDBCol md="3">
                {" "}
                <Button
                  className="CategoriesButton"
                  variant="outline-secondary"
                  id="button-addon2"
                >
                  View More
                </Button>
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <div className="allCards">
                {dataCard.map((elem, index) => {
                  if (elem.category === "Escapes") {
                    return (
                      <div key={index} id={elem.id} className="allCard">
                        <Card className="cards">
                          <Card.Img variant="top" src={elem.img} />
                          {dataCard.newTOSite ? "New TO Site" : " "}
                          <Card.Body>
                            <Card.Title>
                              <h3>
                                {" "}
                                {elem.title.replace(/^(.{20}[^\s]*).*/, "$1")}
                              </h3>
                              <h4>
                                {" "}
                                {elem.subTitle.replace(
                                  /^(.{20}[^\s]*).*/,
                                  "$1"
                                )}
                              </h4>
                              <h6>{elem.country}</h6>
                              <div className="overlay">
                                <div className="text"> {elem.title}</div>
                              </div>
                            </Card.Title>
                            <Card.Text className="cardText">
                              <h6>
                                {" "}
                                {elem.numberBough
                                  ? ` ${elem.numberBough} bought`
                                  : " "}
                              </h6>
                              <h4>
                                <span>From</span>
                                <br /> {elem.price
                                  ? ` $ ${elem.price} `
                                  : " "}{" "}
                              </h4>
                            </Card.Text>
                            {elem.button ? (
                              <Button
                                className="cardButton"
                                variant="outline-secondary"
                                id="button-addon2"
                              >
                                View Collection
                              </Button>
                            ) : null}
                          </Card.Body>
                        </Card>
                      </div>
                    );
                  }
                })}
              </div>
            </MDBRow>
          </div>
          <div className="card">
            <MDBRow>
              <MDBCol md="3">
                {" "}
                <h2 style={{ textAlign: "left" }}>
                  Activities, Events & Outdoors
                </h2>
              </MDBCol>
              <MDBCol md="3">
                {" "}
                <Button
                  className="CategoriesButton"
                  variant="outline-secondary"
                  id="button-addon2"
                >
                  View More
                </Button>{" "}
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <div className="allCards">
                {dataCard.map((elem, index) => {
                  if (elem.category === "Activities, Events & Outdoors") {
                    return (
                      <div key={index} id={elem.id} className="allCard">
                        <Card className="cards">
                          <Card.Img variant="top" src={elem.img} />
                          {dataCard.newTOSite ? "New TO Site" : " "}
                          <Card.Body>
                            <Card.Title>
                              <h3>
                                {" "}
                                {elem.title.replace(/^(.{20}[^\s]*).*/, "$1")}
                              </h3>
                              <h4>
                                {" "}
                                {elem.subTitle.replace(
                                  /^(.{20}[^\s]*).*/,
                                  "$1"
                                )}
                              </h4>
                              <h6>{elem.country}</h6>{" "}
                              <div className="overlay">
                                <div className="text"> {elem.title}</div>
                              </div>
                            </Card.Title>
                            <Card.Text className="cardText">
                              <h6>
                                {" "}
                                {elem.numberBough
                                  ? ` ${elem.numberBough} bought`
                                  : " "}
                              </h6>
                              <h4>
                                <span>From</span>
                                <br /> {elem.price
                                  ? ` $ ${elem.price} `
                                  : " "}{" "}
                              </h4>
                            </Card.Text>
                            {elem.button ? (
                              <Button
                                className="cardButton"
                                variant="outline-secondary"
                                id="button-addon2"
                              >
                                View Collection
                              </Button>
                            ) : null}
                          </Card.Body>
                        </Card>
                      </div>
                    );
                  }
                })}
              </div>
            </MDBRow>
          </div>
          <div className="card">
            <MDBRow>
              <MDBCol md="3">
                {" "}
                <h2 style={{ textAlign: "left" }}>Store </h2>
              </MDBCol>
              <MDBCol md="3">
                {" "}
                <Button
                  className="CategoriesButton"
                  variant="outline-secondary"
                  id="button-addon2"
                >
                  View More
                </Button>
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <div className="allCards">
                {dataCard.map((elem, index) => {
                  if (elem.category === "Store") {
                    return (
                      <div key={index} id={elem.id} className="allCard">
                        <Card className="cards">
                          <Card.Img variant="top" src={elem.img} />
                          {dataCard.newTOSite ? "New TO Site" : " "}
                          <Card.Body>
                            <Card.Title>
                              <h3>
                                {" "}
                                {elem.title.replace(/^(.{20}[^\s]*).*/, "$1")}
                              </h3>
                              <h4>
                                {" "}
                                {elem.subTitle.replace(
                                  /^(.{20}[^\s]*).*/,
                                  "$1"
                                )}
                              </h4>
                              <h6>{elem.country}</h6>{" "}
                              <div className="overlay">
                                <div className="text"> {elem.title}</div>
                              </div>
                            </Card.Title>
                            <Card.Text className="cardText">
                              <h6>
                                {" "}
                                {elem.numberBough
                                  ? ` ${elem.numberBough} bought`
                                  : " "}
                              </h6>
                              <h4>
                                <span>From</span>
                                <br /> {elem.price
                                  ? ` $ ${elem.price} `
                                  : " "}{" "}
                              </h4>
                            </Card.Text>
                            {elem.button ? (
                              <Button
                                className="cardButton"
                                variant="outline-secondary"
                                id="button-addon2"
                              >
                                View Collection
                              </Button>
                            ) : null}
                          </Card.Body>
                        </Card>
                      </div>
                    );
                  }
                })}
              </div>
            </MDBRow>
          </div>
          <div className="card">
            <MDBRow>
              <MDBCol md="3">
                {" "}
                <h2 style={{ textAlign: "left" }}>
                  Restaurants, Bars, Cafes
                </h2>{" "}
              </MDBCol>
              <MDBCol md="3">
                {" "}
                <Button
                  className="CategoriesButton"
                  variant="outline-secondary"
                  id="button-addon2"
                >
                  View More
                </Button>
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <div className="allCards">
                {dataCard.map((elem, index) => {
                  if (elem.category === "Restaurants, Bars, Cafes") {
                    return (
                      <div key={index} id={elem.id} className="allCard">
                        <Card className="cards">
                          <Card.Img variant="top" src={elem.img} />
                          {dataCard.newTOSite ? "New TO Site" : " "}
                          <Card.Body>
                            <Card.Title>
                              <h3>
                                {" "}
                                {elem.title.replace(/^(.{20}[^\s]*).*/, "$1")}
                              </h3>
                              <h4>
                                {" "}
                                {elem.subTitle.replace(
                                  /^(.{20}[^\s]*).*/,
                                  "$1"
                                )}
                              </h4>
                              <h6>{elem.country}</h6>{" "}
                              <div className="overlay">
                                <div className="text"> {elem.title}</div>
                              </div>
                            </Card.Title>
                            <Card.Text className="cardText">
                              <h6>
                                {" "}
                                {elem.numberBough
                                  ? ` ${elem.numberBough} bought`
                                  : " "}
                              </h6>
                              <h4>
                                <span>From</span>
                                <br /> {elem.price
                                  ? ` $ ${elem.price} `
                                  : " "}{" "}
                              </h4>
                            </Card.Text>
                            {elem.button ? (
                              <Button
                                className="cardButton"
                                variant="outline-secondary"
                                id="button-addon2"
                              >
                                View Collection
                              </Button>
                            ) : null}
                          </Card.Body>
                        </Card>
                      </div>
                    );
                  }
                })}
              </div>
            </MDBRow>
          </div>
          <div className="card">
            <MDBRow>
              <MDBCol md="3">
                <h2 style={{ textAlign: "left" }}>Beauty, Massage & Spa</h2>
              </MDBCol>
              <MDBCol md="3">
                {" "}
                <Button
                  className="CategoriesButton"
                  variant="outline-secondary"
                  id="button-addon2"
                >
                  View More
                </Button>{" "}
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <div className="allCards">
                {dataCard.map((elem, index) => {
                  if (elem.category === "Beauty, Massage & Spa") {
                    return (
                      <div key={index} id={elem.id} className="allCard">
                        <Card className="cards">
                          <Card.Img
                            className="imgCard"
                            variant="top"
                            src={elem.img}
                          />
                          {dataCard.newTOSite ? "New TO Site" : " "}
                          <Card.Body>
                            <Card.Title>
                              <h3>
                                {" "}
                                {elem.title.replace(/^(.{20}[^\s]*).*/, "$1")}
                              </h3>
                              <h4>
                                {" "}
                                {elem.subTitle.replace(
                                  /^(.{20}[^\s]*).*/,
                                  "$1"
                                )}
                              </h4>
                              <h6>{elem.country}</h6>{" "}
                              <div className="overlay">
                                <div className="text"> {elem.title}</div>
                              </div>
                            </Card.Title>
                            <Card.Text className="cardText">
                              <h6>
                                {" "}
                                {elem.numberBough
                                  ? ` ${elem.numberBough} bought`
                                  : " "}
                              </h6>
                              <h4>
                                <span>From</span>
                                <br /> {elem.price
                                  ? ` $ ${elem.price} `
                                  : " "}{" "}
                              </h4>
                            </Card.Text>
                            {elem.button ? (
                              <Button
                                className="cardButton"
                                variant="outline-secondary"
                                id="button-addon2"
                              >
                                View Collection
                              </Button>
                            ) : null}
                          </Card.Body>
                        </Card>
                      </div>
                    );
                  }
                })}
              </div>
            </MDBRow>
          </div>
          <div className="card">
            <MDBRow>
              <MDBCol md="3">
                {" "}
                <h2 style={{ textAlign: "left" }}>House & Garden</h2>
              </MDBCol>
              <MDBCol md="3">
                {" "}
                <Button
                  className="CategoriesButton"
                  variant="outline-secondary"
                  id="button-addon2"
                >
                  View More
                </Button>
              </MDBCol>{" "}
            </MDBRow>
