              <div className="allCards">
                {dataCard.map((elem, index) => {
                  if (elem.category === "House & Garden") {
                    return (
                      <div key={index} id={elem.id} className="allCard">
                        <Card className="cards">
                          <Card.Img variant="top" src={elem.img} />
